import React, { useState } from 'react';
import './App.css';

function App() {
  const [ingredients, setIngredients] = useState('');
  const [recommendation, setRecommendation] = useState('');
  const [loading, setLoading] = useState(false);

  const handleGetRecommendation = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:3001/api/recommend', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ ingredients: ingredients.split(',') }),
      });

      if (!response.ok) {
        throw new Error('Failed to get recipe recommendation.');
      }

      const data = await response.json();
      setRecommendation(data.recommendation);
    } catch (error) {
      console.error(error);
      setRecommendation('Failed to get recipe recommendation.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Fridge-Up</h1>
        <p>What's in your fridge?</p>
        <textarea
          placeholder="Enter ingredients, separated by commas (e.g., chicken, broccoli, rice)"
          value={ingredients}
          onChange={(e) => setIngredients(e.target.value)}
        />
        <button onClick={handleGetRecommendation} disabled={loading}>
          {loading ? 'Getting Recommendation...' : 'Get Recommendation'}
        </button>
        {recommendation && (
          <div className="recommendation">
            <h2>Recommendation:</h2>
            <p>{recommendation}</p>
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
